// movies is the state.
const reducer = (movies = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;

        case 'FETCH_ID':
            return movies.movies.find((m) => m._id === action.payload._id);

        default:
            return movies;
    }
}

export default reducer;
