import React from 'react';
import { Card, Container, CardActions, CardContent, CardMedia, Button, Typography, Grid, createSvgIcon, SvgIcon } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import StarsIcon from '@mui/icons-material/Stars';
import useStyles from './styles';


const MovieDetails = ({ movie }) => {
    const classes = useStyles();

    return (
        <Card className={classes.card}>
            <CardMedia id='BAKA' className={classes.media} image={movie.posterurl} title={movie.title}/>
            <div className={classes.overlay}>
                <Typography variant="h4">{movie.title}</Typography>
                <Typography variant="h6">{movie.year}</Typography> 
                <div className={classes.details}>
                    <StarsIcon sx={{ fontSize: 30}}></StarsIcon>
                    <Typography variant="h6">&nbsp; {movie.imdbRating}/10</Typography>
                </div>         
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'yellow'}} size="small">
                    <FavoriteBorderIcon fontSize='large' />
                </Button>
            </div>
        </Card>

    );
}

export default MovieDetails;
