import React from 'react';
import { Container, Card, CardActions, CardContent, CardMedia, Button, Typography, Grid, createSvgIcon, SvgIcon, Popover } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import StarsIcon from '@mui/icons-material/Stars';
import useStyles from './styles';
// import { useDispatch } from 'react-redux';

import MovieDetails from '../MovieDetails/MovieDetails';

// import { getMovieById } from '../../../actions/movies';

const Movie = ({ movie }) => {
    const classes = useStyles();
    const [open, setPopOpen] = React.useState(false);
    // const dispatch = useDispatch();

    const popoverClick = (event) => {
        // setAnchorEl(event.currentTarget);
        setPopOpen(true);
    };

    const popoverClose = () => {
        console.log("CLOSE ME")
        setPopOpen(false);
        // setAnchorEl(null);
    };

    // const open = Boolean(anchorEl);
    const id = open ? 'simple-popover' : undefined;

    const handleChildElementClick = (e) => {
        e.stopPropagation();
        console.log("hey");
    }

    return (
        <Container>
            <Card className={classes.card} onClick={() => setPopOpen(true)}>
                <CardMedia className={classes.media} image={movie.posterurl} title={movie.title}/>
                <div className={classes.overlay}>
                    <Typography variant="h4">{movie.title}</Typography>
                    <Typography variant="h6">{movie.year}</Typography> 
                    <div className={classes.details}>
                        <StarsIcon sx={{ fontSize: 30}}></StarsIcon>
                        <Typography variant="h6">&nbsp; {movie.imdbRating}/10</Typography>
                    </div>         
                </div>
                <div className={classes.overlay2}>
                    <Button style={{color: 'yellow'}} size="small" onClick={(e) => handleChildElementClick(e)}>
                        <FavoriteBorderIcon fontSize='large' />
                    </Button>
                </div>
            </Card>
            <Popover
                id={id}
                open={open}
                anchorReference={'none'}
                classes={{
                    root: classes.popoverRoot,
                    }}
            >
                <Button onClick={() => setPopOpen(false)}>
                    sdf
                </Button>
                <MovieDetails movie={movie} />
            </Popover>
        </Container>
    );
}

export default Movie;
