import React, { useState, useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid, Button } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

import { useDispatch } from 'react-redux';

import { getMovieInfo, getMovies } from './actions/movies.js';
import Movies from './components/Movies/Movies.js';
import useStyles from './styles.js';



const App = () => {
    const [currentId, setCurrentId] = useState(null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect((currentId) => {
        if(currentId){
            dispatch(getMovieInfo());
        } else {
            dispatch(getMovies());
        }
    }, [dispatch]);

    return (
        <Container maxWidth="lg">
            <AppBar className={classes.appBar} position="static" color="inherit">
                <Typography className={classes.heading} variant="h2" align="center">Movies</Typography>
                <Button style={{color: 'yellow'}} size="small" onClick={() => {}}>
                    <FavoriteBorderIcon fontSize='large' />
                </Button>
            </AppBar>
            <Grow in>
                <Container>
                    <Movies currentId={currentId} setCurrentId={setCurrentId} />
                </Container>
            </Grow>
        </Container>
    )
}

export default App;
