import React from 'react';
import { Card, Container, CardActions, CardContent, CardMedia, Button, Typography, Grid, createSvgIcon, SvgIcon, Popover } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import StarsIcon from '@mui/icons-material/Stars';
import useStyles from './styles';


const MovieDetails = ({ movie }) => {
    const classes = useStyles();

    return (
        <Container className={classes.div}>
            <Container>
                <img alt='MoviePoster' src={movie.posterurl}></img>
            </Container>
            <Card>
                <Typography variant='h2'>{movie.title}</Typography>
                <CardContent>{movie.storyLine}</CardContent>
                <Container style={{ display: 'flex', flexDirection: 'row' }}>
                {movie.actors.map((actor) => (
                    <Typography>{actor }</Typography>
                ))}
                </Container>
            </Card>

        </Container>
    );
}

export default MovieDetails;
