import React from 'react';
import { Card, Container, CardActions, CardContent, CardMedia, Button, Typography, Box, Grid, createSvgIcon, SvgIcon, Popover } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import StarsIcon from '@mui/icons-material/Stars';
import useStyles from './styles';


const MovieDetails = ({ movie }) => {
    const classes = useStyles();

    return (
        <Container className={classes.div}>
            <Container>
                <img alt='MoviePoster' src={movie.posterurl}></img>
            </Container>
            <Card>
                <Typography variant='h2'>{movie.title}</Typography>
                <Typography variant='h6'>{movie.year}</Typography>
                <CardContent styles={{ width: '50%'}}>{movie.storyLine}</CardContent>
                <Container style={{ display: 'flex', flexDirection: 'row' }}>
                    <Container>
                        <Button  style={{ color: 'yellow' }} size="small">
                            <FavoriteBorderIcon fontSize='large' />
                        </Button>
                    </Container>
                    <Container style={{ display: 'flex', flexDirection: 'column' }}>
                    <Typography variant='h8'>Cast: {movie.actors[0]}, {movie.actors[1]}, {movie.actors[2]}</Typography>
                    {movie.genres.map((genre) => (
                        <Box>{genre}</Box>
                    ))}
                    </Container>
                </Container>
            </Card>

        </Container>
    );
}

export default MovieDetails;
