import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  div: {
    display: 'flex',
    flexDirection: 'row',
  },
  container: {
    width: 'fit-content',
    height: 'fit-content',
  },
  media: {
    paddingBottom: '10%',
  },
  card: {
    justifyContent: 'space-between',
    borderRadius: '15px',
    color: 'white',
    backgroundColor: '#000050',
  },
  title: {
    padding: '0 16px',
  },
  year: {
    padding: '0 30px',
  },
  summary: {
    padding: '50px 0 0 0',
    justifyContent: 'start',
  },
  rating: {
    display: 'flex',
    justifyContent: 'start',
    padding: '50px 0 50px 0',
  },
  favorite: {
    position: 'absolute',
    bottom: '20px',
    left: '475px',
    justifyContent: 'start',
    color: 'yellow',

  },
  details: {
    display: 'flex',
    direction: 'row',
    position: 'relative',
    padding: '50px 20px 0 20px',
  }
});
