import React from 'react';
import { useEffect } from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography, Grid, createSvgIcon, SvgIcon } from '@material-ui/core';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import StarsIcon from '@mui/icons-material/Stars';
import useStyles from './styles';
import { useDispatch } from 'react-redux';

import { getMovieById } from '../../../actions/movies';
import FavoriteBorder from '@mui/icons-material/FavoriteBorder';

const Movie = ({ movie }) => {
    const classes = useStyles();
    const dispatch = useDispatch();
    var favorite = false; 

    const handleChildElementClick = (e) => {
        e.stopPropagation()
        favorite = !favorite; 
        console.log(favorite)
    }

    return (
        <Card className={classes.card} onClick={() => dispatch(getMovieById(movie.id))}>
            <CardMedia className={classes.media} image={movie.posterurl} title={movie.title}/>
            <div className={classes.overlay}>
                <Typography variant="h4">{movie.title}</Typography>
                <Typography variant="h6">{movie.year}</Typography> 
                <div className={classes.details}>
                    <StarsIcon sx={{ fontSize: 30}}></StarsIcon>
                    <Typography variant="h6">&nbsp; {movie.imdbRating}/10</Typography>
                </div>         
            </div>
            <div className={classes.overlay2}>
                <Button style={{color: 'yellow'}} size="small" onClick={(e) => handleChildElementClick(e)}>
                    {favorite ? <FavoriteIcon fontSize='large' /> : <FavoriteBorderIcon fontSize='large' />}
                </Button>
            </div>
        </Card>
    );
}

export default Movie;
