import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  media: {
    // height: 0,
    // backgroundBlendMode:  'darken',
    paddingBottom: '10%',
  },
  border: {
    border: 'solid',
  },
  fullHeightCard: {
    height: '100%',
  },
  card: {
    // display: 'flex',
    // flexDirection: 'column',
    // position: 'absolute',
    justifyContent: 'space-between',
    borderRadius: '15px',
    height: '200%',
    color: 'white',
    backgroundColor: '#000050',
  },
  overlay: {
    position: 'absolute',
    bottom: '20px',
    left: '20px',
    color: 'white',
  },
  overlay2: {
    position: 'absolute',
    bottom: '20px',
    right: '20px',
    color: 'white',
  },
  grid: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    justifyContent: 'start',
    // margin: '10px',
  },
  title: {
    padding: '0 16px',
  },
  cardActions: {
    padding: '0 16px 8px 16px',
    display: 'flex',
    justifyContent: 'space-between',
  },
  div: {
    display: 'flex',
    flexDirection: 'row',
  },
});
