import { FETCH_ALL, FETCH_ID, ADD_FAVORITE } from "../constants/actionTypes";

// movies is the state.
const reducer = (movies = [], action) => {
    switch (action.type) {
        case FETCH_ALL:
            return action.payload;

        case FETCH_ID:
            return action.payload;

        case ADD_FAVORITE:
            // return movies;
            // const check = movies.map((movie) => (movie._id === action.payload._id ? action.payload: movie));
            return movies.map((movie) => (movie._id === action.payload._id ? action.payload: movie));
            // console.log(check);
            // console.log("hello")
            // return check;

        default:
            return movies;
    }
}

export default reducer;
