import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  media: {
    paddingBottom: '10%',
  },
  container: {
    width: 'fit-content',
    height: 'fit-content',
  },
  card: {
    justifyContent: 'space-between',
    borderRadius: '15px',
    color: 'white',
    backgroundColor: '#000050',
  },
  title: {
    padding: '0 16px',
  },
  year: {
    padding: '0 30px',
  },
  summary: {
    padding: '50px 0 0 0',
    justifyContent: 'start',
  },
  div: {
    display: 'flex',
    flexDirection: 'row',
  },
});
