import { accordionActionsClasses } from "@mui/material";

// movies is the state.
const reducer = (movies = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.payload;

        case 'FETCH_ID':
            return movies.find((movie) => {
                if(movie._id === action.payload._id) {
                    return movie;
                }
            });

        default:
            return movies;
    }
}

export default reducer;
